property.code:
require(['jquery', 'visualisationAPI', 'xwiki-meta'], function($, visualisationAPI, xm) {
    const solrServiceURL = new XWiki.Document('SuggestSolrService', 'XWiki').getURL('get');
    var tempData = null;
    var edgeKeyCounter = 1;

    #template('colorThemeInit.vm') // For XWiki color theme variables
    var themeColors = $jsontool.serialize({
        'nodeColor': $theme.linkColor,
        'labelColor': $theme.textColor,
        'fadeColor': $theme.highlightColor,
        'labelContainerColor': $theme.pageContentBackgroundColor
    });


    function escapeQueryChars(s) {
        const specialCharacters = "\\+-!()^[]\"{}~*?|&;:/";
        let escapedString = "";
        for (let i = 0; i < s.length; i++) {
            const c = s.charAt(i);
            if (specialCharacters.includes(c) || /\s/.test(c)) {
                escapedString += '\\';
            }
            escapedString += c;
        }
        return escapedString;
    }

    const documentQuery = 'document\\:xwiki\\:' + escapeQueryChars(xm.document);
    const linkQuery = 'entity\\:' + documentQuery;

    $.post(solrServiceURL, {
        outputSyntax: 'plain',
        nb: 1000,
        media: 'json',
        query: [
            'q=reference:' + documentQuery,
            'fq=type:DOCUMENT',
            'fl=title_, reference, links, wiki, spaces, name'
        ].join('\n'),
        input: " "
    }, function(firstResult) {
        if (firstResult.length == 1) {
            let extraDocuments = "";
            if (firstResult[0].links) {
                extraDocuments = ' OR ' + firstResult[0].links.map(link => 'reference:' + escapeQueryChars(link.replace(/^entity:/, ''))).join(' OR ');
            }
            $.post(solrServiceURL, {
                outputSyntax: 'plain',
                nb: 1000,
                media: 'json',
                query: [
                    'q=reference:' + documentQuery + ' OR links:' + linkQuery + extraDocuments,
                    'fq=type:DOCUMENT',
                    'fl=title_, reference, links, wiki, spaces, name'
                ].join('\n'),
                input: " "
            }, function(response) {
                tempData = response;
                const nodes = tempData.map(function(obj) {
                    return {
                        key: obj.reference,
                        attributes: {
                            label: obj.title_,
                            color: themeColors.nodeColor,
                            reference: obj.reference,
                            pageURL: new XWiki.Document(XWiki.Model.resolve(obj.reference)).getURL()
                        }
                    };
                });

                const edges = [];
                tempData.forEach(function(obj) {
                    if (obj.links && obj.links.length > 0) {
                        obj.links.forEach(function(link) {
                            var target = link.replace(/^entity:/, '');
                            var isValid = tempData.some(function(item) {
                                return item.reference === target;
                            });
                            if (isValid) {
                                edges.push({
                                    key: edgeKeyCounter.toString(),
                                    target: target,
                                    source: obj.reference
                                });
                                edgeKeyCounter++;
                            }
                        });
                    }
                });
                const graphData = {
                    nodes: nodes,
                    edges: edges
                };

                visualisationAPI.visualize(graphData, "sigma-container-panel", themeColors, isPanel = true, nb);

            });
        }
    });
});
